import React from 'react';
import { BrowserRouter as Router, Route, Link, Switch } from "react-router-dom";
import { useNavigate } from 'react-router-dom';  // Import useNavigate hook
import MainRate from './table';
import './App.css';
import Conv from './cal';

const NotFound = () => {
  return <h2>404 Not Found</h2>;
}

const App = () => {
  const navigate = useNavigate();  // Initialize navigate hook

  return (
    <Router>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <Link className="navbar-brand" to="/">Currency Converter</Link>
        <button onClick={() => navigate('/cal')} className="btn btn-primary">
          Go to Converter
        </button>
      </nav>
      <Switch>
        <Route path="/" exact component={MainRate} />
        <Route path="/cal" component={Conv} /> {/* Fix the path to '/cal' */}
        <Route component={NotFound} /> {/* Catch all route for 404 */}
      </Switch>
      <footer>
        <p>By Ravicha, Guided by Altcademy</p>
      </footer>
    </Router>
  );
}

export default App;
