import React from 'react';
import { Link } from "react-router-dom";
import { json, checkStatus } from './util';

const CurrencyRates = ({ base, date, rates }) => {
  return (
    <div className="row">
      <div className="col-8 col-md-10 col-lg-11 mb-3">
        <Link to={`/rates/${base}/`}>
          <h4>Top {base} Exchange Rate</h4>
          <p>{date}</p>
          <table>
            <thead>
                <tr>
                    <th>Currency</th>
                    <th>Rate</th>
                    <th>Inv. Rate</th>
                </tr>
            </thead>
            <tbody>
            {Object.entries(rates).map(([currency, rate]) => (
                <tr key = {currency}>
                    <td className='w-25'>{currency}</td>
                    <td className='w-25'>{rate}</td>
                    <td className='w-25'>{(1/rate).toFixed(5)}</td>
                </tr>
                ))}


            </tbody>
        </table>
        </Link>
      </div>
    </div>
  );
};
const watchlist = ({base, rates}) => {
    return (
        <div className='row'>
            <div className='col-6 col-md-10 col-lg-11 mb-3'>

            </div>
        </div>
    );
};

class MainRate extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchTerm: '',
      result: null,  // Changed to store only a single result object, not an array
      error: '',
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ searchTerm: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    let { searchTerm } = this.state;
    searchTerm = searchTerm.trim();
    if (!searchTerm) {
      return;
    }

    fetch(`https://api.frankfurter.app/latest?base=${searchTerm}`)
      .then(checkStatus)
      .then(json)
      .then((data) => {
        this.setState({ result: data, error: '' });
      })
      .catch((error) => {
        this.setState({ error: error.message });
        console.error(error);
      });
  }

  render() {
    const { searchTerm, result, error } = this.state;

    return (
      <div className="container">
        <div className="row">
          <div className="col-3">
            <form onSubmit={this.handleSubmit} className="form-inline my-4">
              <input
                type="text"
                className="form-control mr-sm-2"
                placeholder="USD"
                value={searchTerm}
                onChange={this.handleChange}
              />
              <button type="submit" className="btn btn-primary mt-2">Submit</button>
            </form>
          </div>
          <div className='col-8 my-4 mx-3'>
          {error ? (
              <p>{error}</p>
            ) : (
              result && (
                <CurrencyRates
                  base={result.base}
                  date={result.date}
                  rates={result.rates}
                />
              )
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default MainRate;
